mapper.select:
select * from(select RANK() OVER(order by play_ref desc) no , p.* from
(select * from play 





	play_genrenm = #{genrenm} 

		and  play_prfnm Like '%'||#{prfnm}||'%' 
		 and play_locnm Like '%'||#{locnm}||'%' 
		and  (play_from Like '%'||#{fromto}||'%' or play_to Like
			'%'||#{fromto}||'%')  





) p) where no between #{start} AND #{end}

select * from(select RANK() OVER(order by play_ref desc) no , p.* from (select * from play
play_prfnm Like '%'||#{prfnm}||'%'
or play_locnm Like '%'||#{locnm}||'%'
or play_from Like '%'||#{fromto}||'%' or play_to Like '%'||#{fromto}||'%' or play_genrenm Like '%'||#{genrenm}||'%'
) p) where no between #{start} AND #{end}
